mapper.select:
select * from job_resume
where 1=1

  and id = #{id}


  and job_id = #{jobId}


  and create_user_id = #{createUserId}


  and create_time = #{createTime}


  and update_user_id = #{updateUserId}


  and update_time = #{updateTime}


  and user_id = #{userId}


  and status = #{status}

